package com.loop;

public class Program6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//sum of odd no bet 1 to 20
		
		int num1=0;
		for (int i=1;i<=20;i++)
		{
			if (i%2==1)
			{
				
				num1=num1+i;
			}
			
		}
		
		System.out.println(num1);
	}

}
